              </label>
            </div>
            <div class="form-check">
              <label class="form-check-label" for="radio2">
                <input
                  type="radio"
                  value="DBPEDIA"
                  class="form-check-input"
                  checked={this.state.size === "DBPEDIA"}
                  onChange={this.handleChange}
                />
