  // };
  constructor(props) {
    super(props);
    this.state = {
      classname: "",
      sname: null
    };
  }
  myChangeHandler = event => {
    let nam = event.target.name;
    let val = event.target.value;
    if (nam === "sname") {
      if (!Number(val)) {
        alert("Your age must be a number");
      }
    }
    this.setState({ [nam]: val });
  };
