  // };
  sendData = () => {
    this.props.parentCallback("Hey Popsie, How’s it going?");
  };
  render() {
    const greeting = "gggggggggggggggggggggggggggggg";

    return (
      // <React.Fragment>
      //   <form id="form">
      //     <h1>
      //       Hello {this.state.username} {this.state.age}
      //     </h1>
      //     <p>
      //       <i>
      //         {" "}
      //         <input
      //           type="text"
      //           className="input"
      //           placeholder="Class"
      //           id="classInput"
      //           onChange={this.myChangeHandler}
      //         />
      //       </i>
      //       <i>
      //         <input
      //           type="text"
      //           className="input"
      //           placeholder="Search..."
      //           id="searchInput"
      //           onChange={this.myChangeHandler}
      //         />{" "}
      //       </i>
      //     </p>
      //     {/* <input type="submit" onclick="cname();" /> */}
      //   </form>
      // </React.Fragment>
      <React.Fragment>
        <form>
          <h1>
            Hello {this.state.classname} {this.state.sname}
          </h1>
          <p>Enter your name:</p>
          <input type="text" name="classname" onChange={this.myChangeHandler} />
          <p>Enter your age:</p>
          <input type="text" name="sname" onChange={this.myChangeHandler} />
        </form>
        ReactDOM.render(
        <Gen color={this.props.sname} />, document.getElementById("gen") );
      </React.Fragment>
    );
  }
}

export default Search;

function cname() {
  var cinput = document.getElementById("classInput").value;
  alert("dscdscdscdscs");
}

